import React from "react";
class TodoItems extends React.Component {
    render() {
        // destructuring là 1 cách viết code được sử dụng để lưu lại dữu  liệu được truyền vào từ các class khác
        const { completed, id, title } = this.props.todo // cách viết 1 destructuring giúp cho code ngắn gọn hơn
        return (
            <li className={ this.props.todo.completed ? "completed todo-item" : "todo-item"}>
                {/* Attribute checked bên đưới dùng đẻ lưu lại trạng thái của checkbok ( có tích là true, không thì là false) */}
                <input type="checkbox" checked={this.props.todo.completed} onChange = {() => {this.props.handleChange(this.props.todo.id)}}/>
                <span>
                    {this.props.todo.title}
                </span>
            </li>
        );
    };
}
export default TodoItems;
