import React from "react";
class TodoItems extends React.Component {
    render() {
        return (
            <li className="todo-item">
                {/* Attribute checked bên đưới dùng đẻ lưu lại trạng thái của checkbok ( có tích là true, không thì là false) */}
                <input type="checkbox" checked={this.props.todo.completed} onChange = {() => {this.props.handleChange(this.props.todo.id)}}/>{this.props.todo.title}</li>
        );
    };
}
export default TodoItems;
