import React from 'react';
import TodoItems from './TodoItems'
class Todos extends React.Component {
    render() {
        return (
            <div>
                <ul>
                    {/* Truyền dữ liệu từ state trong class Todoapps TodoApp.js sang Todo.js qua phương thức props  */}
                    {this.props.todos.map(todo => (
                        <TodoItems key={todo.id} todo={todo} handleChange = {todo.id}/>))}
                </ul>
            </div>
        );
    };
};
export default Todos;
