import React from "react";
import axios from "axios";
class data{
    todos = [];

    assign (id){
        if(id<=3){
            this.todos[id].completed = true;
        }
    };

    init(){
        console.log("skdyufiudy")
        axios.post("https://jsonplaceholder.typicode.com/todos")
                .then(response => {
                    response.map(todo =>{
                        const items = {
                            title: todo.title,
                            completed: todo.completed
                        }
                        this.todos.push(items);
                    })
                }
            )
    };

}
export default data;
