import React from "react";
import Header from "./layout/Header";
import Todos from "./Todos";
class TodoApp extends React.Component {
    state = {
        todos: [
            {
                id:1,
                title: "Setup Appearance",
                completed: true,
            },
            {
                id: 2,
                title: "Create parent component",
                completed: true,
            },
            {
                id: 3,
                title: "Create method component",
                completed: false,
            }
        ]
    };
    handleCheckBoxChange = id => {
        // Hàm setState() dùng để thay đổi thuộc tính trong state 
        this.setState({
            todos: this.state.todos.map(todo => {
                if(todo.id == id){
                    todo.completed = !todo.completed;
                }
                return todo;
            })
        });
    };
    deleteTodo = id => {
        cons
        this.setState({
            todos: this.state.todos.map(todo => {
                todo = null;
                
            })
        })

    };
        
    render() {
        return (
        <div className="container">
            <Header />
            
            <Todos todos = {this.state.todos} handleChange = {this.handleCheckBoxChange} deleteTodo={this.deleteTodo}/>
        </div>);
    };
}
export default TodoApp;
