import React from "react";
const green = "#BAF516";
const red = "#ff0000";
const gray = "#678c89";
class Footer extends React.Component {
    // Khai báo cấu trúc class trong react redux
    constructor(props) {
        super(props); // KHởi tạo biến parent trong hàm để hàm xử dụng được con trỏ this
        this.submitThemeColor = this.submitThemeColor.bind(this); // hàm bind ở đây dùng để tạo lại một hàm có chức năng
        // giống hàm submitTHemeColor
    }
    // Hàm xử lý giá trị mã màu 
    submitThemeColor(color) {
        if (color) {
            console.log('handleChangeTheme')
            this.props.saveColorTheme(color);
            }
    }
    componentWillReceiveProps(nextprops){
        console.log('UNSAFE_componentWillReceiveProps: ' +JSON.stringify(nextprops))
        document
        .documentElement
        .style
        .setProperty("--main-color", nextprops.themeColor.color);
        }
        
    render() {
        return (
            <div className="footer">
                <div className="vertical-center">
                    <span>Choose Theme </span>
                        <button onClick={()=>this.submitThemeColor(green)} className="dot red"/>
                        <button onClick={()=>this.submitThemeColor(red)} className="dot blue"/>
                        <button onClick={()=>this.submitThemeColor(gray)} className="dot gray"/>
                </div>
            </div>
        );
    }
};
export default Footer;
