import React from "react";
import Header from "./layout/Header";
import Todos from "./Todos";
import AddTodo from "./AddTodo";
class TodoApp extends React.Component {
    state = {
        todos: [
            {
                id:1,
                title: "Setup Appearance",
                completed: true,
            },
            {
                id: 2,
                title: "Create parent component",
                completed: true,
            },
            {
                id: 3,
                title: "Create method component",
                completed: false,
            }
        ]
    };
    
    handleCheckBoxChange = id => {
        // Hàm setState() dùng để thay đổi thuộc tính trong state 
        this.setState({
            todos: this.state.todos.map(todo => {
                if(todo.id == id){
                    todo.completed = !todo.completed;
                }
                return todo;
            })
        });
    };
    deleteTodo = id => {
        this.setState({
            todos: 
                // Method filter giúp xóa các phần tử trong object
                this.state.todos.filter(todo => {
                    return todo.id !==id;
                })
        })
    };

    addTodo = title => {
        var stateId = this.state.todos.length;
            stateId++;
        console.log(stateId);
        const newTodo = {
            id: stateId,
            title: title,
            completed: false
            };
            this.setState({
            todos: [...this.state.todos, newTodo]
            });
    };
        
    render() {
        return (
        <div className="container">
            <Header />
            <AddTodo addTodo={this.addTodo}/> 
            <Todos todos = {this.state.todos} handleChange = {this.handleCheckBoxChange} deleteTodo={this.deleteTodo}/>
        </div>);
    };
}
export default TodoApp;
